package com.samourai.whirlpool.server.controllers.rest;

import static org.springframework.boot.test.context.SpringBootTest.WebEnvironment.RANDOM_PORT;

import com.samourai.javaserver.exceptions.RestException;
import com.samourai.whirlpool.protocol.v0.rest.PushTxErrorResponse;
import com.samourai.whirlpool.protocol.v0.rest.Tx0DataRequestV2;
import com.samourai.whirlpool.protocol.v0.rest.Tx0DataResponseV2;
import com.samourai.whirlpool.protocol.v0.rest.Tx0PushRequest;
import com.samourai.whirlpool.server.beans.Pool;
import com.samourai.whirlpool.server.beans.PoolFee;
import com.samourai.whirlpool.server.integration.AbstractIntegrationTest;
import java.lang.invoke.MethodHandles;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.test.context.SpringBootTest;

@SpringBootTest(webEnvironment = RANDOM_PORT)
public class Tx0ControllerTest extends AbstractIntegrationTest {
  private static final Logger log = LoggerFactory.getLogger(MethodHandles.lookup().lookupClass());

  @Autowired private Tx0Controller tx0Controller;

  @BeforeEach
  @Override
  public void setUp() throws Exception {
    super.setUp();

    Pool pool = poolService.getPool("0.01btc");
    PoolFee poolFee = new PoolFee(FEES_VALID, null);
    pool._setPoolFee(poolFee);
  }

  @Test
  public void tx0Data_noScode() throws Exception {
    Tx0DataRequestV2 request = new Tx0DataRequestV2(null, null);
    Tx0DataResponseV2 response = tx0Controller.tx0Data(null, request);

    // 100% fee (0% SCODE discount)
    for (Tx0DataResponseV2.Tx0Data tx0Data : response.tx0Datas) {
      Assertions.assertEquals(0, tx0Data.feeDiscountPercent);
      Assertions.assertTrue(tx0Data.feeValue > 0);
    }
  }

  @Test
  public void pushTx0_feePayloadValid_0() throws Exception {
    String txHex =
        "";
    Tx0PushRequest request = new Tx0PushRequest(tx64FromTxHex(txHex), "0.01btc");

    try {
      tx0Controller.tx0Push(request);
    } catch (RestException e) {
      PushTxErrorResponse response = (PushTxErrorResponse) e.getResponse();
      Assertions.assertEquals("bad-txns-inputs-missingorspent", response.message);
      Assertions.assertEquals("bad-txns-inputs-missingorspent", response.pushTxErrorCode);
    }
  }

  @Test
  public void pushTx0_feePayloadValid_noScode() throws Exception {
    try {
      String txHex =
          "";
      Tx0PushRequest request = new Tx0PushRequest(tx64FromTxHex(txHex), "0.01btc");
      tx0Controller.tx0Push(request);
    } catch (RestException e) {
      PushTxErrorResponse response = (PushTxErrorResponse) e.getResponse();
      Assertions.assertEquals("bad-txns-inputs-missingorspent", response.message);
      Assertions.assertEquals("bad-txns-inputs-missingorspent", response.pushTxErrorCode);
    }
  }
}
